import { mapActions } from "vuex";
import BagComponent from "./BagComponent.vue";
import CardWithDescriptionComponent from "./CardWithDescriptionComponent.vue";
export default {
  components: {
    CardWithDescriptionComponent,
    BagComponent
  },
  name: "ShoppingLayout",
  props: ["title", "path"],
  data: () => ({
    products: null
  }),
  methods: { ...mapActions(["addToBag"]),

    async getProducts(path) {
      try {
        const response = await fetch(path);
        if (!response.ok) throw "Error en API";
        const json = await response.json();
        this.products = json.productos;
      } catch (error) {
        console.log(error);
      }
    }

  },

  // watch: {},
  // components: {},
  // mixins: [],
  // filters: {},
  created() {
    this.getProducts(this.path);
  } // -- Lifecycle Methods
  // -- End Lifecycle Methods


};
