export default {
  name: "FormComponent",
  data: () => ({
    valid: false,
    disabledForm: false,
    datos: {},
    datosComprador: [{
      name: "name",
      label: "Nombre"
    }, {
      name: "email",
      label: "Email",
      type: "email"
    }, {
      name: "repeatEmail",
      label: "Repetir Email",
      type: "email",
      rules: ["repeatEmail"]
    }, {
      name: "phone",
      label: "Teléfono"
    }],
    datosDespacho: [{
      name: "direction",
      label: "Dirección"
    }, {
      name: "comuna",
      label: "Comuna"
    }],
    formaPago: [{
      value: 1,
      label: "Transferencia Bancaria"
    }, {
      value: 2,
      label: "Servipag"
    }, {
      value: 3,
      label: "Webpay"
    }, {
      value: 4,
      label: "Contra entrega"
    }]
  }),
  methods: {
    getRules(input) {
      const rules = {
        text: v => !!v || "Este campo es obligatorio",
        email: v => /.+@.+\..+/.test(v) || "Ingrese un email valido",
        repeatEmail: v => this.datos.email == v || "Ingrese el mismo email"
      };
      return [rules[input.type || "text"], ...(input.rules || []).map(rule => rules[rule])];
    },

    submitForm() {
      this.$refs.form.validate();
      if (!this.valid) return;
      this.datos["orden"] = Date.now();
      this.$emit("submitForm", this.datos);
    }

  }
};
